import React, {Component} from "react";
import { makeStyles } from "@material-ui/core/styles";
import { withStyles } from '@material-ui/core/styles';
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Input from "@material-ui/core/Input";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    marginTop: theme.spacing(3),
    overflowX: "auto"
  },
  table: {
    minWidth: 650
  },
  selectTableCell: {
    width: 60
  },
  tableCell: {
    width: 130,
    height: 40
  },
  input: {
    width: 130,
    height: 40
  }
}));

const createCustomer = (id, name, address, phone, orders) => ({
    customer_id: id, 
    customer_name: name,
    customer_address: address,
    customer_phone: phone,
    orders: orders,
    isEditMode: false
});

const CustomTableCell = ({ row, name, onChange }) => {
  const classes = useStyles();
  const { isEditMode } = row;
  return (
    <TableCell align="left" className={classes.tableCell}>
      {isEditMode ? (
        <Input
          value={row[name]}
          name={name}
          onChange={e => onChange(e, row)}
          className={classes.input}
        />
      ) : (
        row[name]
      )}
    </TableCell>
  );
};

class Customers extends Component{
    constructor(){
        super();
        this.state = {
            customers: []
        };
    }
    componentDidMount(){
        fetch('/customers')
            .then(res => {
                console.log(res);
                return res.json()
             })
            .then(rows => { 
                var cust = []
                console.log(rows);
                rows.forEach(row => {
                    console.log(row);
                    cust.push(createCustomer(row.customer_id, row.customer_name, row.customer_address, row.customer_phone, row.orders));
                });
                console.log(cust[0].orders.toString());
                this.setState({ customers: cust });
             });
    }

    
    render(){
      const { classes } = this.props;
        return (
          <Paper className={classes.root}>
            <Table className={classes.table} aria-label="caption table">
              <caption>A barbone structure table example with a caption</caption>
              <TableHead>
                <TableRow>
                  <TableCell align="left">Customer ID</TableCell>
                  <TableCell align="left">Customer Name</TableCell>
                  <TableCell align="left">Address</TableCell>
                  <TableCell align="left">Phone</TableCell>
                  <TableCell align="left">Orders</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                { this.state.customers.map(row => (
                  <TableRow key={row.customer_id}>
                    <CustomTableCell {...{ row, name: "customer_id" }} />
                    <CustomTableCell {...{ row, name: "customer_name" }} />
                    <CustomTableCell {...{ row, name: "customer_address" }} />
                    <CustomTableCell {...{ row, name: "customer_phone" }} />
                    <CustomTableCell {...{ row, name: "orders" }.toString()} />
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        );
    }
}

export default withStyles(useStyles)(Customers);
