import React, {Component} from "react";
import { makeStyles } from "@material-ui/core/styles";
import { withStyles } from '@material-ui/core/styles';
import Table from "@material-ui/core/Table";
import IconButton from "@material-ui/core/IconButton";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Modal from '@material-ui/core/Modal';
import Box from '@material-ui/core/Box';
import Typography from "@material-ui/core/Typography";
import EditIcon from '@material-ui/icons/Edit';

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    marginTop: theme.spacing(3),
    overflowX: "auto"
  },
  table: {
    minWidth: 650
  },
  selectTableCell: {
    width: 60
  },
  tableCell: {
    width: 130,
    height: 40
  },
  input: {
    width: 130,
    height: 40
  }
}));

const createProduct = (id, name, qty, sku) => ({
    product_id: id, 
    product_name: name,
    qty: qty,
    sku: sku,
    isEditMode: false
});

const CustomTableCell = ({ row, name, onChange }) => {
  const classes = useStyles();
  return (
    <TableCell align="left" className={classes.tableCell}>
        {row[name].toString()}
    </TableCell>
  );
};

class Products extends Component{
    constructor(){
        super();
        this.state = {
            products: [],
            editModal: {
                status: false,
                id: 0
            }
        };
    }
    componentDidMount(){
        fetch('/products')
            .then(res => {
                console.log(res);
                return res.json()
             })
            .then(rows => { 
                var prod = []
                console.log(rows);
                rows.forEach(row => {
                    console.log(row);
                    prod.push(createProduct(row.product_id, row.product_name, row.qty, row.sku));
                });
                this.setState({ products: prod });
             });
    }
    
    render(){
      const { classes } = this.props;
        return (
          <Paper className={classes.root}>
            <editModal id={this.state.editModal.id} status={this.state.editModal.status}/>
            <Table className={classes.table} aria-label="caption table">
              <caption>A barbone structure table example with a caption</caption>
              <TableHead>
                <TableRow>
                  <TableCell align="left">Product ID</TableCell>
                  <TableCell align="left">Product Name</TableCell>
                  <TableCell align="left">Quantity</TableCell>
                  <TableCell align="left">SKU</TableCell>
                  <TableCell align="left">Edit</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                { this.state.products.map(row => (
                  <TableRow key={row.product_id}>
                    <CustomTableCell {...{ row, name: "product_id" }} />
                    <CustomTableCell {...{ row, name: "product_name" }} />
                    <CustomTableCell {...{ row, name: "qty" }} />
                    <CustomTableCell {...{ row, name: "sku" }} />
                    <TableCell align="left"><IconButton onClick={() => {
                        console.log(row.product_id);
                        this.setState({ editModal: { status: true, id: row.product_id } });
                    }}><EditIcon /></IconButton></TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        );
    }
}

export default withStyles(useStyles)(Products);
